// Helper function to get unique categories and subcategories
export const getUniqueCategories = (products) => {
    const categories = new Set();
    const subCategories = {}; // { category: Set<subCategory> }

    products.forEach(product => {
        categories.add(product.category);
        if (!subCategories[product.category]) {
            subCategories[product.category] = new Set();
        }
        if (product.subCategory) {
            subCategories[product.category].add(product.subCategory);
        }
    });

    return {
        categories: Array.from(categories),
        subCategories: Object.fromEntries(
            Object.entries(subCategories).map(([cat, subCatSet]) => [cat, Array.from(subCatSet)])
        )
    };
};
